mapper.select:
select * from review 
where room_num > 0

and room_num like concat('%', #{searchRoom_num}, '%')


and user_id like concat('%',#{searchUser_id},'%')


 and rev_regdate >= #{startDate} 


 and rev_regdate <= #{endDate} 


order by rev_num desc 
limit #{pageStart}, #{amount};

select count(*) as total from review
where room_num > 0	

and room_num like concat('%', #{searchRoom_num}, '%')


and user_id like concat('%',#{searchUser_id},'%')


 and rev_regdate >= #{startDate} 


 and rev_regdate <= #{endDate}
